li.OKResult:

if me:GetProperty("MaxAge") - me:GetAge() < 100 then
	me:AddMsg(XT("[NAME]的寿元已经不足一百年……"));
else

me:AddMsg(XT("[NAME]购买成功！寿元减少一百年！"));
me:ModifierProperty("MaxAge",-100);
me:DropAwardItem("Item_huobi",1);
me:TriggerStory("Secrets_SouMing_1");
end


if me:GetProperty("MaxAge") - me:GetAge() < 1000 then
	me:AddMsg(XT("[NAME]的寿元已经不足一千年……"));
else

me:AddMsg(XT("[NAME]购买成功！寿元减少一千年！"));
me:ModifierProperty("MaxAge",-1000);
me:DropAwardItem("Item_huobi",10);
me:TriggerStory("Secrets_SouMing_1");
end


if me:GetProperty("MaxAge") - me:GetAge() < 10000 then
	me:AddMsg(XT("[NAME]的寿元已经不足一万年……"));
else

me:AddMsg(XT("[NAME]购买成功！寿元减少一万年！"));
me:ModifierProperty("MaxAge",-10000);
me:DropAwardItem("Item_huobi",100);
me:TriggerStory("Secrets_SouMing_1");
end
